import React, { useState, useEffect } from 'react';
import CourseList from './components/CourseList';
import CourseDetail from './components/CourseDetail';

const App = () => {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [userRatings, setUserRatings] = useState({});
  const [courses, setCourses] = useState([]); // now starts empty

  // ðŸ”— Fetch courses from backend
  useEffect(() => {
    fetch("http://localhost:8080/api/courses")
      .then((res) => res.json())
      .then((data) => setCourses(data))
      .catch((err) => console.error("Error fetching courses:", err));
  }, []);

  const handleRatingChange = (courseId, rating) => {
    setUserRatings(prev => ({
      ...prev,
      [courseId]: rating
    }));
  };

  const getSuitableCourses = () => {
    return courses.filter(course => {
      const rating = userRatings[course.id];
      return rating === 'excellent' || rating === 'very good' || rating === 'good';
    });
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <header className="text-center mb-12">
        <h1 className="text-4xl font-bold text-blue-800 mb-4">Career Guidance Application</h1>
        <p className="text-gray-600">Rate your skills and discover the perfect learning path for you</p>
      </header>

      {!selectedCourse ? (
        <CourseList
          courses={courses}
          userRatings={userRatings}
          onRatingChange={handleRatingChange}
          onCourseSelect={setSelectedCourse}
          suitableCourses={getSuitableCourses()}
        />
      ) : (
        <div>
          <button
            onClick={() => setSelectedCourse(null)}
            className="mb-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Back to Courses
          </button>
          <CourseDetail course={selectedCourse} />
        </div>
      )}
    </div>
  );
};

export default App;
