import React, { useEffect, useState } from 'react';
import CourseList from './CourseList';
import CourseDetail from './CourseDetail';

const RecommendationPage = ({ userRatings }) => {
  const [recommendedCourses, setRecommendedCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  useEffect(() => {
    console.log("User Ratings in RecommendationPage:", userRatings); // Log the prop
    const cachedData = localStorage.getItem('cachedRecommendations');
        if (cachedData) {
          setRecommendedCourses(JSON.parse(cachedData));
        } else if (userRatings) {
      fetch('http://localhost:8081/api/recommendations', { // CORRECTED ENDPOINT
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          "Programming Skills": userRatings.programming,
          "Cyber Security": userRatings.cybersecurity,
          "Communication Skills": userRatings.communication,
          "Aptitude": userRatings.aptitude
        })
      })
        .then(res => res.json())
        .then(data => {
          console.log("API Response:", data);
          setRecommendedCourses(data);
        })
        .catch(err => console.error("Error fetching recommendations:", err));
    }
  }, [userRatings]);

  if (selectedCourse) {
    return <CourseDetail course={selectedCourse} />;
  }

  return (
    <CourseList
      courses={recommendedCourses}
      onCourseSelect={setSelectedCourse}
      suitableCourses={recommendedCourses}
    />
  );
};

export default RecommendationPage;
