import React, { useState, useEffect } from 'react';
import CourseList from './components/CourseList';

const App = () => {
  const [userRatings, setUserRatings] = useState({});
  const [suitableCourses, setSuitableCourses] = useState([]);
  const [courses, setCourses] = useState([]); // To store all courses

  // Fetch all available courses once
  useEffect(() => {
    const fetchAllCourses = async () => {
      try {
        const response = await fetch('http://localhost:8081/api/courses');  // GET courses
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      }
    };

    fetchAllCourses();
  }, []);

  // Fetch recommended courses based on user ratings
  useEffect(() => {
    const fetchRecommendedCourses = async () => {
      try {
        const response = await fetch('http://localhost:8081/api/recommendations', {  // POST to recommendations endpoint
          method: 'POST',
          body: JSON.stringify(userRatings),
          headers: {
            'Content-Type': 'application/json',
          },
        });

        const data = await response.json();

        // Check if the response is an array and set the suitable courses
        if (Array.isArray(data)) {
          setSuitableCourses(data);
        } else {
          console.error('Response is not an array:', data);
        }
      } catch (error) {
        console.error('Error fetching recommendations:', error);
      }
    };

    if (Object.keys(userRatings).length > 0) {  // Only fetch recommendations if userRatings is populated
      fetchRecommendedCourses();
    }
  }, [userRatings]);  // Run this whenever userRatings changes

  return (
    <div className="App">
      <CourseList
        courses={courses}
        userRatings={userRatings}
        suitableCourses={suitableCourses}
        onRatingChange={(id, rating) => setUserRatings(prev => ({ ...prev, [id]: rating }))}
        onCourseSelect={(course) => console.log(course)}
      />
    </div>
  );
};

export default App;
