import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SkillForm.css';

const SkillForm = ({ username }) => { // Receive username as a prop
  const navigate = useNavigate();

  const initialSkills = {
    // ... your initial skills
  };

  const [ratings, setRatings] = useState(initialSkills);

  const handleChange = (skill, value) => {
    setRatings({ ...ratings, [skill]: value });
  };

  const handleSubmit = () => {
    const selectedSkills = Object.keys(ratings).filter(
      key => ratings[key] === 'Select'
    );

    sessionStorage.setItem('selectedSkills', JSON.stringify(selectedSkills));

    navigate('/recommendations'); // Keep this as it is
  };

  const ratingOptions = ['Select'];

  const skillLabels = {
    // ... your skill labels
  };

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow">
      <div className="flex justify-end mb-2">
        <span className="text-lg font-semibold">Hi! {username}</span> {/* Display username */}
      </div>
      <div className="mb-4 text-center">
        <h2>About Our Career Guidance App</h2>
        <p className="text-gray-700">This application is designed to help you discover relevant courses based on your self-assessed skills. By rating your proficiency in various technical areas, you'll receive personalized recommendations to guide your learning journey.</p>
        {/* Add more detailed information about your app here */}
      </div>
      <div className="logo-section">
        <img src="https://upload.wikimedia.org/wikipedia/commons/a/a7/React-icon.svg" alt="Logo" />
      </div>
      <h2 className="text-3xl font-bold mb-6 text-center">Rate Your Skills</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {Object.keys(ratings).map(skill => (
          <div key={skill}>
            <label className="block mb-1 font-medium">{skillLabels[skill]}</label>
            <select
              value={ratings[skill]}
              onChange={(e) => handleChange(skill, e.target.value)}
              className="w-full border px-3 py-2 rounded"
            >
              <option value="">Choose your option</option>
              {ratingOptions.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <div className="text-center mt-6">
        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Get Recommendations
        </button>
      </div>
    </div>
  );
};

export default SkillForm;
