import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SkillForm = ({ setUserRatings }) => {
  const navigate = useNavigate();

  const initialSkills = {
    ai_ml: '',
    data_science: '',
    business_analyst: '',
    cyber_security: '',
    database_admin: '',
    database_fundamentals: '',
    computer_architecture: '',
    software_development: '',
    programming_skills: '',
    project_management: '',
    technical_communication: '',
    distributed_systems: '',
    computer_network: '',
    computer_forensic: ''
  };

  const [ratings, setRatings] = useState(initialSkills);

  const handleChange = (skill, value) => {
    setRatings({ ...ratings, [skill]: value });
  };

  const handleSubmit = () => {
    setUserRatings(ratings);
    navigate('/recommendations');
  };

  const ratingOptions = ['Select'];

  const skillLabels = {
    ai_ml: 'AI ML',
    data_science: 'Data Science',
    business_analyst: 'Business Analyst',
    cyber_security: 'Cyber Security',
    database_admin: 'Database Administrator',
    database_fundamentals: 'Database Fundamentals',
    computer_architecture: 'Computer Architecture',
    software_development: 'Software Development',
    programming_skills: 'Programming Skills',
    project_management: 'Project Management',
    technical_communication: 'Technical Communication Skills',
    distributed_systems: 'Distributed Computer Systems',
    computer_network: 'Computer Network',
    computer_forensic: 'Computer Forensic'
  };

  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-3xl font-bold mb-6 text-center">Rate Your Skills</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {Object.keys(ratings).map(skill => (
          <div key={skill}>
            <label className="block mb-1 font-medium">{skillLabels[skill]}</label>
            <select
              value={ratings[skill]}
              onChange={(e) => handleChange(skill, e.target.value)}
              className="w-full border px-3 py-2 rounded"
            >
              <option value="">Select Rating</option>
              {ratingOptions.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <div className="text-center mt-6">
        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          Get Recommendations
        </button>
      </div>
    </div>
  );
};

export default SkillForm;
