import React, { useState } from 'react';
import './App.css'; // For your styling

function App() {
  const [ratings, setRatings] = useState({
    programming: '',
    cybersecurity: '',
    communication: '',
    aptitude: ''
  });

  const [courses, setCourses] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  const handleRatingChange = (skill, value) => {
    setRatings(prev => ({ ...prev, [skill]: value }));
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:8081/api/recommendations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          "Programming Skills": ratings.programming,
          "Cyber Security": ratings.cybersecurity,
          "Communication Skills": ratings.communication,
          "Aptitude": ratings.aptitude
        })
      });
      const data = await response.json();
      setCourses(data);
      setSubmitted(true);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
    }
  };

  return (
    <div className="container">
      <h1>Rate Your Skills</h1>

      {["programming", "cybersecurity", "communication", "aptitude"].map((skill, i) => (
        <div key={i} className="rating-group">
          <label>{skill.charAt(0).toUpperCase() + skill.slice(1).replace(/([A-Z])/g, ' $1')}</label>
          <select
            value={ratings[skill]}
            onChange={(e) => handleRatingChange(skill, e.target.value)}
          >
            <option value="">Select Rating</option>
            <option value="Professional">Professional</option>
            <option value="Excellent">Excellent</option>
            <option value="Very Good">Very Good</option>
            <option value="Good">Good</option>
            <option value="Poor">Poor</option>
            <option value="Not Interested">Not Interested</option>
          </select>
        </div>
      ))}

      <button onClick={handleSubmit}>Get Recommendations</button>

      {submitted && (
        <div className="recommendations">
          <h2>Recommended Courses</h2>
          {courses.length > 0 ? (
            courses.map((course, index) => (
              <div key={index} className="course-card">
                <h3>{course.name}</h3>
                <p>{course.description}</p>
                <a href={course.resources.courseLink} target="_blank" rel="noreferrer">Course Link</a>
              </div>
            ))
          ) : (
            <p>No recommendations found.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
