import React, { useState, useEffect } from 'react';
import CourseList from './components/CourseList';

const App = () => {
  const [courses, setCourses] = useState([]); // Store all available courses
  const [userRatings, setUserRatings] = useState({});
  const [suitableCourses, setSuitableCourses] = useState([]);

  useEffect(() => {
    // Fetch all courses
    const fetchCourses = async () => {
      const response = await fetch('http://localhost:8081/api/courses'); // Adjust if needed
      const data = await response.json();
      setCourses(data);
    };
    fetchCourses();
  }, []);

  useEffect(() => {
    // Fetch recommended courses based on user ratings
    const fetchRecommendedCourses = async () => {
      const response = await fetch('http://localhost:8081/api/recommendations', {
        method: 'POST',
        body: JSON.stringify(userRatings),
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();
      if (Array.isArray(data)) {
        setSuitableCourses(data);
      } else {
        console.error('Response is not an array');
      }
    };

    if (Object.keys(userRatings).length > 0) {
      fetchRecommendedCourses();
    }
  }, [userRatings]);

  return (
    <div className="App">
      <CourseList
        courses={courses} // ✅ Pass the course list here
        userRatings={userRatings}
        suitableCourses={suitableCourses}
        onRatingChange={(id, rating) => setUserRatings(prev => ({ ...prev, [id]: rating }))}
        onCourseSelect={(course) => console.log(course)}
      />
    </div>
  );
};

export default App;
