import React from 'react';
import './CourseDetail.css';

const CourseDetail = ({ course, onGoBack }) => {
  if (!course) {
    return <div>Loading...</div>;
  }

  const {
    name = 'No course name available',
    description = 'No description available',
    topics = [],
    resources = {}
  } = course;

  const {
    courseLink = '',
    youtubeLinks = [],
    materials = []
  } = resources;

  return (
    <div className="course-detail-container">
      <h2 className="course-title">{name}</h2>

      {/* Overview Section */}
      <div className="section">
        <h3 className="section-title">Overview</h3>
        <p className="section-content">{description}</p>
      </div>

      {/* Key Topics */}
      <div className="section">
        <h3 className="section-title">Key Topics</h3>
        <ul className="topics-list">
          {Array.isArray(topics) && topics.length > 0 ? (
            topics.map((topic, index) => (
              <li key={index} className="topic-item">{topic}</li>
            ))
          ) : (
            <p>No topics available.</p>
          )}
        </ul>
      </div>

      {/* Course Resources */}
      <div className="section">
        <h3 className="section-title">Course Resources</h3>
        <div className="resources-list">
          {/* Main Course Link */}
          <div className="resource-item">
            <h4 className="resource-title">Main Course Link:</h4>
            {courseLink ? (
              <a
                href={courseLink}
                target="_blank"
                rel="noopener noreferrer"
                className="resource-link"
              >
                Access Course
              </a>
            ) : (
              <p>No course link available.</p>
            )}
          </div>

          {/* Tutorial Videos */}
          <div className="resource-item">
            <h4 className="resource-title">Tutorial Videos:</h4>
            {Array.isArray(youtubeLinks) && youtubeLinks.length > 0 ? (
              <>
                <p className="info-note">
                  Please watch all {youtubeLinks.length} tutorial videos before proceeding.
                </p>
                <ul className="youtube-links">
                  {youtubeLinks.map((link, index) => (
                    <li key={index}>
                      <a
                        href={link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="resource-link"
                      >
                        Video {index + 1}
                      </a>
                    </li>
                  ))}
                </ul>
              </>
            ) : (
              <p>No tutorial videos available.</p>
            )}
          </div>

          {/* Additional Materials */}
          <div className="resource-item">
            <h4 className="resource-title">Additional Materials:</h4>
            {Array.isArray(materials) && materials.length > 0 ? (
              <ul className="materials-list">
                {materials.map((material, index) => (
                  <li key={index} className="material-item">{material}</li>
                ))}
              </ul>
            ) : (
              <p>No additional materials available.</p>
            )}
          </div>
        </div>
      </div>

      {/* Back Button */}
      <button onClick={onGoBack} className="back-button">Back</button>
    </div>
  );
};

export default CourseDetail;
