import React, { useEffect, useState } from 'react';
import '../index.css';

const CourseList = ({ userRatings, onRatingChange, onCourseSelect, suitableCourses }) => {
  const [courses, setCourses] = useState([]);
  const ratingOptions = ['excellent', 'very good', 'good', 'poor', 'not interested'];

  // Static courses array (can be replaced with backend API call later)
  const coursesData = [
    { id: 1, name: "Programming Skills" },
    { id: 2, name: "Cyber Security" },
    { id: 3, name: "Communication Skills" },
    { id: 4, name: "Aptitude" },
    // Add other courses from GitHub or your source
  ];

  // Update the courses state
  useEffect(() => {
    setCourses(coursesData);
  }, []);

  // Filter the suitable courses based on ratings
  useEffect(() => {
    const filteredCourses = courses.filter(course => {
      const rating = userRatings[course.id];
      return rating && ['excellent', 'very good', 'good'].includes(rating.toLowerCase());
    });

    suitableCourses(filteredCourses);
  }, [userRatings, courses, suitableCourses]);

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
        <h2 className="text-2xl font-semibold mb-4">Rate Your Skills</h2>
        <div className="space-y-6">
          {courses.map(course => (
            <div key={course.id} className="border-b pb-4">
              <h3 className="text-xl font-medium mb-2">{course.name}</h3>
              <div className="flex items-center space-x-4">
                <span className="text-gray-600">Your Rating:</span>
                <select
                  value={userRatings[course.id] || ''}
                  onChange={(e) => onRatingChange(course.id, e.target.value)}
                  className="border rounded px-3 py-1"
                >
                  <option value="">Select Rating</option>
                  {ratingOptions.map(option => (
                    <option key={option} value={option}>
                      {option.charAt(0).toUpperCase() + option.slice(1)}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          ))}
        </div>
      </div>

      {Object.keys(userRatings).length > 0 && (
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Recommended Courses</h2>
          {suitableCourses.length > 0 ? (
            <div className="grid gap-4">
              {suitableCourses.map(course => (
                <div
                  key={course.id}
                  className="border rounded-lg p-4 hover:bg-gray-50 cursor-pointer"
                  onClick={() => {
                    console.log('Selected course:', course); // ✅ proper logging
                    onCourseSelect(course); // ✅ triggers course detail view
                  }}
                >
                  <h3 className="text-xl font-medium mb-2">{course.name}</h3>
                  <p className="text-gray-600">{course.description}</p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">Based on your ratings, try improving your skills in some areas to get course recommendations.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default CourseList;
