import React, { useEffect, useState, useRef } from 'react';
import CourseList from '../components/CourseList';
import CourseDetail from '../components/CourseDetail';
import './RecommendationPage.css';

const RecommendationPage = () => {
  const [recommendedCourses, setRecommendedCourses] = useState([]);
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  useEffect(() => {
    const skillsFromStorage = JSON.parse(sessionStorage.getItem('selectedSkills') || '[]');
    setSelectedSkills(skillsFromStorage);

    if (Array.isArray(skillsFromStorage) && skillsFromStorage.length > 0) {
      fetch('http://localhost:8081/api/recommendations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ selectedSkills: skillsFromStorage })
      })
        .then(res => {
          if (!res.ok) {
            throw new Error('Failed to fetch recommendations');
          }
          return res.json();
        })
        .then(data => {
          setRecommendedCourses(data);
          sessionStorage.setItem('recommendedCourses', JSON.stringify(data));
        })
        .catch(err => console.error('Error fetching recommendations:', err));
    }
  }, []);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
  };

  const handleGoBack = () => {
    setSelectedCourse(null);
  };

  const goToSkillForm = () => {
    navigate('/skillform'); // Navigate to the /skillform route (assuming useNavigate is available in this component or a parent)
  };

  if (selectedCourse) {
    return <CourseDetail course={selectedCourse} onGoBack={handleGoBack} />;
  }

  return (
    <div className="recommendation-page-container">
      <div className="recommendation-header"> {/* New container for heading and button */}
        <h2 className="page-title">Recommended Courses</h2>
        <button className="back-to-skillform-button header-button" onClick={goToSkillForm}>
          Back to Select Skills
        </button>
      </div>

      {selectedSkills.length > 0 && (
        <div className="selected-skills-section">
          <h3 className="section-heading">Skills You Selected:</h3>
          <p className="selected-skills-list">{selectedSkills.join(', ')}</p>
        </div>
      )}

      {recommendedCourses.length === 0 ? (
        <div className="no-recommendations-container"> {/* Container for no recommendations message and button */}
          <p className="no-recommendations">
            No matching course recommendations found for your selected skills. Please try again with different skills.
          </p>
          <button className="back-to-skillform-button centered-button" onClick={goToSkillForm}>
            Back to Select Skills
          </button>
        </div>
      ) : (
        <div className="recommendations-list-container wider-container">
          <p className="recommendations-info">
            Based on your recommendation, below are the details of the courses.
          </p>
          <CourseList
            courses={recommendedCourses}
            onCourseSelect={handleCourseSelect}
            suitableCourses={recommendedCourses}
          />
        </div>
      )}
    </div>
  );
};

export default RecommendationPage;
