import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SkillForm = ({ setUserRatings }) => {
  const navigate = useNavigate();
  const [ratings, setRatings] = useState({
    programming: '',
    cybersecurity: '',
    communication: '',
    aptitude: '',
    
  });

  const handleChange = (skill, value) => {
    setRatings({ ...ratings, [skill]: value });
  };

  const handleSubmit = () => {
    setUserRatings(ratings); // save to App state
    navigate('/recommendations'); // go to results page
  };

  const ratingOptions = ['Excellent', 'Very Good', 'Good', 'Poor', 'Not Interested'];

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Rate Your Skills</h2>
      {Object.keys(ratings).map(skill => (
        <div key={skill} className="mb-4">
          <label className="block mb-1 capitalize">{skill}</label>
          <select
            value={ratings[skill]}
            onChange={(e) => handleChange(skill, e.target.value)}
            className="w-full border px-3 py-2 rounded"
          >
            <option value="">Select Rating</option>
            {ratingOptions.map(option => (
              <option key={option} value={option}>{option}</option>
            ))}
          </select>
        </div>
      ))}
      <button
        onClick={handleSubmit}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Get Recommendations
      </button>
    </div>
  );
};

export default SkillForm;
