import React from 'react';
import './CourseDetail.css';

const CourseDetail = ({ course }) => {
  // Check if course is undefined, if so return a loading state or message
  if (!course) {
    return <div>Loading...</div>; // You can replace this with a spinner if you prefer
  }

  // Destructure course properties, providing defaults where necessary
  const { 
    name, 
    description, 
    topics = [], 
    resources = {} 
  } = course;
  const { 
    courseLink, 
    youtubeLinks = [], 
    materials = [] 
  } = resources;

  return (
    <div className="course-detail-container">
      <h2 className="course-title">{name || 'Course Name Not Available'}</h2>
      <div className="section">
        <h3 className="section-title">Overview</h3>
        <p className="section-content">{description || 'No description available'}</p>
      </div>

      <div className="section">
        <h3 className="section-title">Key Topics</h3>
        <ul className="topics-list">
          {topics?.length > 0 ? (
            topics.map((topic, index) => (
              <li key={index} className="topic-item">{topic}</li>
            ))
          ) : (
            <p>No topics available.</p>
          )}
        </ul>
      </div>

      <div className="section">
        <h3 className="section-title">Course Resources</h3>
        <div className="resources-list">
          <div className="resource-item">
            <h4 className="resource-title">Main Course Link:</h4>
            {courseLink ? (
              <a
                href={courseLink}
                target="_blank"
                rel="noopener noreferrer"
                className="resource-link"
              >
                Access Course
              </a>
            ) : (
              <p>No course link available.</p>
            )}
          </div>

          <div className="resource-item">
            <h4 className="resource-title">Tutorial Videos:</h4>
            {youtubeLinks?.length > 0 ? (
              <ul className="youtube-links">
                {youtubeLinks.map((link, index) => (
                  <li key={index}>
                    <a
                      href={link}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="resource-link"
                    >
                      Tutorial Video {index + 1}
                    </a>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No tutorial videos available.</p>
            )}
          </div>

          <div className="resource-item">
            <h4 className="resource-title">Additional Materials:</h4>
            {materials?.length > 0 ? (
              <ul className="materials-list">
                {materials.map((material, index) => (
                  <li key={index} className="material-item">{material}</li>
                ))}
              </ul>
            ) : (
              <p>No additional materials available.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
