import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SkillForm.css';
import logo from '../assets/images/logo.png'; // Ensure the path to your logo is correct

const SkillForm = ({ username }) => {
  const navigate = useNavigate();

  const initialSkills = {
    ai_ml: '',
    data_science: '',
    business_analyst: '',
    cyber_security: '',
    database_admin: '',
    database_fundamentals: '',
    computer_architecture: '',
    software_development: '',
    programming_skills: '',
    project_management: '',
    technical_communication: '',
    distributed_systems: '',
    computer_network: '',
    computer_forensic: ''
  };

  const [ratings, setRatings] = useState(initialSkills);

  const handleChange = (skill, value) => {
    setRatings({ ...ratings, [skill]: value });
  };

  const handleSubmit = () => {
    const selectedSkills = Object.keys(ratings).filter(
      key => ratings[key] === 'Select'
    );

    sessionStorage.setItem('selectedSkills', JSON.stringify(selectedSkills));

    navigate('/recommendations');
  };

  const ratingOptions = ['Select'];

  const skillLabels = {
    ai_ml: 'AI ML',
    data_science: 'Data Science',
    business_analyst: 'Business Analyst',
    cyber_security: 'Cyber Security',
    database_admin: 'Database Administrator',
    database_fundamentals: 'Database Fundamentals',
    computer_architecture: 'Computer Architecture',
    software_development: 'Software Development',
    programming_skills: 'Programming Skills',
    project_management: 'Project Management',
    technical_communication: 'Technical Communication Skills',
    distributed_systems: 'Distributed Computer Systems',
    computer_network: 'Computer Network',
    computer_forensic: 'Computer Forensic'
  };

  return (
    <div className="skill-form-container">
      <div className="top-bar">
        <div className="logo-container">
          <img src={logo} alt="App Logo" />
        </div>
        <span className="greeting">Hi! {username}</span> {/* Username directly in top-bar */}
      </div>
      <div className="app-info">
        <p className="app-description">
          Explore your potential! Rate your skills to discover personalized course recommendations and chart your career path.
        </p>
      </div>

      <h2 className="form-title">Rate Your Skills</h2>
      <div className="skills-grid">
        {Object.keys(ratings).map(skill => (
          <div key={skill} className="skill-rating">
            <label htmlFor={skill}>{skillLabels[skill]}</label>
            <select
              id={skill}
              value={ratings[skill]}
              onChange={(e) => handleChange(skill, e.target.value)}
            >
              <option value="">Choose your option</option>
              {ratingOptions.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        ))}
      </div>

      <button className="submit-button" onClick={handleSubmit}>
        Get Personalized Recommendations
      </button>
    </div>
  );
};

export default SkillForm;
