import React, { useEffect, useState } from 'react';
import CourseList from './CourseList';
import CourseDetail from './CourseDetail';

const RecommendationPage = () => {
  const [recommendedCourses, setRecommendedCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);

  useEffect(() => {
    const selectedSkills = JSON.parse(sessionStorage.getItem('selectedSkills') || '[]');
    console.log("Selected skills:", selectedSkills);  // Log the selected skills to check

    // Check if selectedSkills is an array and not empty
    if (Array.isArray(selectedSkills) && selectedSkills.length > 0) {
      fetch('http://localhost:8081/api/courses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ selectedSkills })  // Send the selectedSkills as an array
      })
        .then(res => res.json())
        .then(data => {
          setRecommendedCourses(data);
          sessionStorage.setItem('recommendedCourses', JSON.stringify(data));
        })
        .catch(err => console.error('Error fetching recommendations:', err));
    }
  }, []);

  const handleCourseSelect = (course) => {
    setSelectedCourse(course);
  };

  const handleGoBack = () => {
    setSelectedCourse(null);
  };

  if (selectedCourse) {
    return <CourseDetail course={selectedCourse} onGoBack={handleGoBack} />;
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4 text-center">Recommended Courses</h2>
      {recommendedCourses.length === 0 ? (
        <p className="text-center text-gray-600">No course selected. Please go back and choose a course.</p>
      ) : (
        <div>
          <p className="text-center text-gray-700 mb-4">
            Based on your recommendation, below are the details of the courses.
          </p>
          <CourseList
            courses={recommendedCourses}
            onCourseSelect={handleCourseSelect}
            suitableCourses={recommendedCourses}
          />
        </div>
      )}
    </div>
  );
};

export default RecommendationPage;
