import React from 'react';
import './CourseDetail.css'; // Keep this for the overall layout
import './CourseList.css'; // Import CourseList.css for button styles

const CourseDetail = ({ course, onGoBack }) => {
  if (!course) return <div>Loading...</div>;

  const {
    name = 'No course name available',
    description = 'No description available',
    topics = [],
    resources = {}
  } = course;

  const {
    courseLink = '',
    youtubeLinks = [], // This should contain an array of 5 YouTube links
    materials = []
  } = resources;

  const handleCourseLinkClick = (link) => {
    window.open(link, '_blank'); // Opens the course link in a new tab
  };

  const handleVideoClick = (link) => {
    window.open(link, '_blank'); // Opens the YouTube link in a new tab
  };

  return (
    <div className="course-detail-container">
      <h2 className="course-title">{name}</h2>

      <div className="section">
        <h3 className="section-title">Overview</h3>
        <p className="section-content">{description}</p>
      </div>

      <div className="section">
        <h3 className="section-title">Key Topics</h3>
        <ul className="topics-list">
          {topics.length > 0 ? (
            topics.map((topic, idx) => <li key={idx} className="topic-item">{topic}</li>)
          ) : (
            <p>No topics available.</p>
          )}
        </ul>
      </div>

      <div className="section">
        <h3 className="section-title">Access Course</h3>
        {courseLink ? (
          <button
            onClick={() => handleCourseLinkClick(courseLink)}
            className="header-button-cl" // Using header-button-cl from CourseList.css
          >
            Access Course
          </button>
        ) : (
          <p>No course link available.</p>
        )}
      </div>

      <div className="section">
        <h3 className="section-title">Tutorial Videos</h3>
        <div className="video-buttons">
          {[...Array(5)].map((_, index) => {
            const link = youtubeLinks[index];
            return (
              <button
                key={index}
                onClick={() => link && handleVideoClick(link)} // Only clickable if link exists
                className={`header-button-cl ${!link ? 'disabled' : ''}`} // Using header-button-cl for video buttons
                disabled={!link} // Disable button if there's no YouTube link
              >
                Video {index + 1}
              </button>
            );
          })}
        </div>
        {youtubeLinks.length === 0 && <p>No tutorial videos available.</p>}
      </div>

      <div className="section">
        <h3 className="section-title">Materials</h3>
        {materials.length > 0 ? (
          <ul className="materials-list">
            {materials.map((m, i) => <li key={i}>{m}</li>)}
          </ul>
        ) : (
          <p>No additional materials available.</p>
        )}
      </div>

      <button onClick={onGoBack} className="header-button-cl">Back</button> {/* Using header-button-cl for the back button as well */}
    </div>
  );
};

export default CourseDetail;
